package ch03_1_operator_expression;

public class AssingmentOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int result = 0;
		result += 10;
		System.out.println("result= " + result);

		result -= 5;
		System.out.println("result= " + result);

		result *= 3;
		System.out.println("result= " + result);

		result /= 5;
		System.out.println("result= " + result);

		result %= 3;
		System.out.println("result= " + result);

	}

}
